import { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [title, setTitle] = useState("");
  const [date, setDate] = useState("");
  const [time, setTime] = useState("");
  const [priority, setPriority] = useState("Low");
  const [schedules, setSchedules] = useState([]);

  const API = "http://localhost:5000/schedules";

  const fetchSchedules = async () => {
    const res = await axios.get(API);
    setSchedules(res.data);
  };

  useEffect(() => {
    fetchSchedules();
  }, []);

  const addSchedule = async () => {
    if (!title) return;
    await axios.post(API, { title, date, time, priority });
    setTitle("");
    setDate("");
    setTime("");
    setPriority("Low");
    fetchSchedules();
  };

  const toggleStatus = async (id, completed) => {
    await axios.put(`${API}/${id}`, { completed: !completed });
    fetchSchedules();
  };

  const deleteSchedule = async (id) => {
    await axios.delete(`${API}/${id}`);
    fetchSchedules();
  };

  return (
    <div className="app">
      <h2>Schedule Planner</h2>

      <div className="input-box">
        <input
          placeholder="Task"
          value={title}
          onChange={e => setTitle(e.target.value)}
        />
        <input
          type="date"
          value={date}
          onChange={e => setDate(e.target.value)}
        />
        <input
          type="time"
          value={time}
          onChange={e => setTime(e.target.value)}
        />
        <select
          value={priority}
          onChange={e => setPriority(e.target.value)}
        >
          <option>Low</option>
          <option>Medium</option>
          <option>High</option>
        </select>
        <button onClick={addSchedule}>Add</button>
      </div>

      <ul>
        {schedules.map(item => (
          <li key={item._id} className={item.completed ? "done" : ""}>
            <span onClick={() => toggleStatus(item._id, item.completed)}>
              {item.title} | {item.date} {item.time} | {item.priority}
            </span>
            <button onClick={() => deleteSchedule(item._id)}>‚ùå</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
