import { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";

const API = "http://localhost:5000/schedules";

function App() {
  const [tasks, setTasks] = useState([]);
  const [showNewTask, setShowNewTask] = useState(false);

  const [title, setTitle] = useState("");
  const [date, setDate] = useState("");
  const [time, setTime] = useState("");
  const [notes, setNotes] = useState("");

  const fetchTasks = async () => {
    const res = await axios.get(API);
    setTasks(res.data);
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  const addTask = async () => {
    if (!title.trim()) return;

    await axios.post(API, {
      title,
      date: date || null,
      time: time || null,
      notes: notes || null
    });

    setTitle("");
    setDate("");
    setTime("");
    setNotes("");
    setShowNewTask(false);
    fetchTasks();
  };

  const toggleTask = async (id, completed) => {
    await axios.put(`${API}/${id}`, { completed: !completed });
    fetchTasks();
  };

  const deleteTask = async (id) => {
    await axios.delete(`${API}/${id}`);
    fetchTasks();
  };

  return (
    <div className="container">
      {/* TASKS PANEL */}
      <div className="panel tasks">
        <div className="tasks-header">
          <h2>Tasks</h2>
          <button
            className="new-task-btn"
            onClick={() => setShowNewTask(true)}
          >
            + New Task
          </button>
        </div>

        <ul>
          {tasks.map(task => (
            <li
              key={task.id}
              className={`task ${task.completed ? "done" : ""}`}
              onClick={() => toggleTask(task.id, task.completed)}
            >
              <div>
                <strong>{task.title}</strong>
                <div className="meta">
                  {task.date && <span>{task.date}</span>}
                  {task.time && <span>{task.time}</span>}
                </div>
                {task.notes && (
                  <div className="notes-preview">{task.notes}</div>
                )}
              </div>

              <button
                onClick={(e) => {
                  e.stopPropagation();
                  deleteTask(task.id);
                }}
              >
                ✕
              </button>
            </li>
          ))}
        </ul>
      </div>

      {/* NEW TASK PANEL (POP-UP) */}
      {showNewTask && (
        <div className="panel add slide-in">
          <div className="add-header">
            <h2>New Task</h2>
            <button onClick={() => setShowNewTask(false)}>✕</button>
          </div>

          <input
            placeholder="Task title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />

          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
          />

          <input
            type="time"
            value={time}
            onChange={(e) => setTime(e.target.value)}
          />

          <textarea
            placeholder="Notes (optional)"
            value={notes}
            onChange={(e) => setNotes(e.target.value)}
            rows="4"
          />

          <button className="add-btn" onClick={addTask}>
            Add Task
          </button>
        </div>
      )}
    </div>
  );
}

export default App;
