import { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";

const API = "http://localhost:5000";

function App() {
  const [tasks, setTasks] = useState([]);
  const [notes, setNotes] = useState("");
  const [notifications, setNotifications] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [showNotifications, setShowNotifications] = useState(false);

  const [title, setTitle] = useState("");
  const [date, setDate] = useState("");
  const [time, setTime] = useState("");

  const fetchAll = async () => {
    setTasks((await axios.get(`${API}/schedules`)).data);
    setNotes((await axios.get(`${API}/notes`)).data.content);
    setNotifications((await axios.get(`${API}/notifications`)).data);
  };

  useEffect(() => {
    fetchAll();
  }, []);

  const addTask = async () => {
    await axios.post(`${API}/schedules`, { title, date, time });
    setShowModal(false);
    setTitle(""); setDate(""); setTime("");
    fetchAll();
  };

  const toggleTask = async (id, completed) => {
    await axios.put(`${API}/schedules/${id}`, { completed: !completed });
    fetchAll();
  };

  const unreadCount = notifications.filter(n => !n.read).length;

  return (
    <div className="container">
      {/* HEADER */}
      <div className="top-bar">
        <h2>Schedule Planner</h2>
        <button onClick={() => setShowNotifications(!showNotifications)}>
          ðŸ”” {unreadCount > 0 && <span className="badge">{unreadCount}</span>}
        </button>
      </div>

      {/* PANELS */}
      <div className="content">
        <div className="panel">
          <button className="new-task" onClick={() => setShowModal(true)}>
            + New Task
          </button>

          <ul>
            {tasks.map(t => (
              <li
                key={t.id}
                className={t.completed ? "done" : ""}
                onClick={() => toggleTask(t.id, t.completed)}
              >
                {t.title}
              </li>
            ))}
          </ul>
        </div>

        <div className="panel">
          <h3>Notes</h3>
          <textarea
            value={notes}
            onChange={e => setNotes(e.target.value)}
          />
        </div>
      </div>

      {/* NOTIFICATIONS PANEL */}
      {showNotifications && (
        <div className="notification-panel">
          <h4>Notifications</h4>
          {notifications.map(n => (
            <div key={n.id} className="notification">
              {n.message}
            </div>
          ))}
        </div>
      )}

      {/* MODAL */}
      {showModal && (
        <div className="modal">
          <input placeholder="Title" value={title}
            onChange={e => setTitle(e.target.value)} />
          <input type="date" value={date}
            onChange={e => setDate(e.target.value)} />
          <input type="time" value={time}
            onChange={e => setTime(e.target.value)} />
          <button onClick={addTask}>Add</button>
        </div>
      )}
    </div>
  );
}

export default App;
